public class day6{

	public static void main(String[] args){

		HashMap<String, Integer> list = new HashMap<String, Integer>();

		String s = "";
		

		System.out.println(checkDuplicates(s));

		int startIndex = 0;

		//for solution to part 1 keep this as 4, for part 2 change to 14
		int endIndex = 4;
		String passage = "";

		while(true){

			passage = s.substring(startIndex, endIndex);
			System.out.println(passage);

			if(!checkDuplicates(passage)){
				break;
			}
			startIndex++;
			endIndex++;
		}		
		System.out.println("Solution: " + endIndex);
	}
	public static boolean checkDuplicates(CharSequence s){

		for(int i = 0; i < s.length(); i++){

			for(int j = i + 1; j < s.length(); j++){

				if(s.charAt(i) == s.charAt(j)){

					return true;
				}
			}
		}
		return false;
	}
}